#include<bits/stdc++.h>using namespace std;int prima(int n) {	int f = 1;	for(int i = 2; i <= n; i++) 		f *= i;	return f;}int solve(string s) {	int len = s.length();	int f[26];	memset(f, 0, sizeof(f));	for(int i = 0; i < len; i++) 		if(s[i] >= 'a')			f[s[i] - 'a']++;	int fa = 1;	for(int i = 0; i < 26; i++) 		fa *= prima(f[i]);	return prima(len) / fa;}int main() {	string s;	cin >> s;	cout << solve(s) << endl;}
